import '../styles/menubar.css'
import { useNavigate } from "react-router-dom";
import { useState } from "react";

const MenuBar = () => {
  const navigate = useNavigate();
  const [isOpen, setIsOpen] = useState(false);
  const handleClick = (path) => {
    setIsOpen(false); // 메뉴 닫기
    setTimeout(() => {
      navigate(path); // 지연 후 페이지 이동
    }, 300);
  }

  return(
    <div className={`menubar-container ${isOpen ? 'open' : ''}`}>
      <div className="menubar">
        <button className="repository-icon" onClick={() => handleClick('/storagelist')}>
          <img src="imgs/pexels-chevanon-photography-1108099.jpg" alt="저장소 목록 아이콘"/>
        </button>
        <button className="home-icon" onClick={() => handleClick('/')}>
          <img src="imgs/pexels-eberhard-grossgasteiger-844297.jpg" alt="홈 아이콘"/>
        </button>
        <button className="login-icon" onClick={() => handleClick('/login')}>
          <img src="imgs/pexels-katja-592077.jpg" alt="로그인 아이콘"/>
        </button>
      </div>
    </div>
  );
}

export default MenuBar;
