import React, { useState } from 'react';
import Select from 'react-select';

const App = () => {
  const [selectedOptions, setSelectedOptions] = useState({});
  const [isGroupEnabled, setIsGroupEnabled] = useState(true); // 첫 번째 그룹은 항상 enable

  // 예시 데이터
  const groupListData = {
    countList: [
      { combination: ['스몰', '검정'], remainCount: 0 },
      { combination: ['스몰', '하양'], remainCount: 3 },
      { combination: ['스몰', '빨강'], remainCount: 0 },
      { combination: ['라지', '검정'], remainCount: 5 },
      { combination: ['라지', '하양'], remainCount: 0 },
      { combination: ['라지', '빨강'], remainCount: 1 },
    ],
    titleList: ['사이즈', '색상'],
    groupList: [
      { title: '사이즈', options: ['스몰', '라지'] },
      { title: '색상', options: ['검정', '하양', '빨강'] },
    ],
  };

  const { groupList, titleList, countList } = groupListData;

  // 각 그룹의 옵션을 선택할 때 처리 함수
  const handleGroupChange = (selectedOption, groupIndex) => {
    const updatedOptions = { ...selectedOptions, [groupIndex]: selectedOption };
    setSelectedOptions(updatedOptions);

    // 현재 선택된 그룹 인덱스와 이전 그룹 인덱스를 비교하여 disable 상태 업데이트
    if (groupIndex < groupList.length - 1) {
      setIsGroupEnabled(true); // 현재 그룹 다음의 그룹을 enable
    } else {
      setIsGroupEnabled(false); // 마지막 그룹 선택 시 disable
    }
  };

  // 색상 옵션의 남은 갯수 및 품절 여부 확인 함수
  const getOptionLabel = (color) => {
    const optionData = countList.find(item => item.combination[1] === color);
    if (optionData) {
      if (optionData.remainCount === 0) {
        return `${color} (품절)`;
      } else {
        return `${color} (${optionData.remainCount}개 남음)`;
      }
    }
    return color;
  };

  return (
    <div>
      {groupList.map((group, index) => (
        <div key={index}>
          <h2>{group.title}</h2>
          <Select
            options={group.options.map(option => ({
              value: option,
              label: group.title === '색상' ? getOptionLabel(option) : option,
              isDisabled: group.title === '색상' && countList.find(item => item.combination[1] === option)?.remainCount === 0
            }))}
            onChange={(selectedOption) => handleGroupChange(selectedOption, index)}
            value={selectedOptions[index]}
            placeholder={`Select ${group.title}`}
            isDisabled={!isGroupEnabled || (index > 0 && !selectedOptions[index - 1])} // 첫 번째 그룹은 항상 enable, 이후 그룹은 이전 그룹이 선택되어야 enable
          />
        </div>
      ))}
      <h2>선택한 옵션:</h2>
      {titleList.map((title, index) => (
        <p key={index}>
          {title}: {selectedOptions[index]?.value || '-'}
        </p>
      ))}
    </div>
  );
};

export default App;
