import Header from "../../components/Header";
import '../../styles/storagelist.css';
import { TiFolderOpen } from "react-icons/ti";
import { useState, useEffect } from "react";
import { dbService } from "../../firebase";

const StorageList = () => {
  const formSelectList = [
    { value: "default", name: "형태"},
    { value: "group", name: "그룹형"},
    { value: "list", name: "목록형"},
  ];
  const [formSelected, setFormSelected] = useState("형태");

  const handleFormSelect = (e) => {
    setFormSelected(e.target.value);
    console.log(formSelected);
  };

  const orderSelectList = [
    { value: "default", name: "나열순서"},
    { value: "current", name: "최신순"},
    { value: "register", name: "등록순"},
  ];
  const [orderSelected, setOrderSelected] = useState("형태");

  const handleOrderSelect = (e) => {
    setOrderSelected(e.target.value);
    console.log(orderSelected);
  };

  const [menus, setMenus] = useState("");

  const getMenus = async () => {
    const dbMenus = await dbService.collection("jjalbang-storageName").get();
   //  dbMenus.forEach((document) => {
   //   setMenus((prev) => [document.data(), ...prev]);
   // });
    const fetchedMenus = dbMenus.docs.map(document => document.data());
    setMenus(fetchedMenus);
  };

  useEffect(() => {
    getMenus();
  }, []);

  console.log(menus);

  return(
    <div className="storage-container">
      <Header text={"저장소"}/>
      <div className="storage">
        <img className="storage-background-img" src="imgs/cute-doodle-flower-pattern.png" alt="배경화면" />
        <div className="storage-components">
          <div className="storage-select-container">
            <div className="storage-selects">
              <select className="form-select" onChange={handleFormSelect} value={formSelected}>
                {formSelectList.map((item) => {
                  return <option value={item.value} key={item.value}>
                    {item.name}
                  </option>;
                })}
              </select>
              <select className="order-select" onChange={handleOrderSelect} value={orderSelected}>
                {orderSelectList.map((item) => {
                  return <option value={item.value} key={item.value}>
                    {item.name}
                  </option>;
                })}
              </select>
            </div>
          </div>

          {formSelected === "group" ? (
            <div className="storage-menu-group">
            {/*<label className="menu-group-label">*/}
            {/*  <TiFolderOpen style={{width: "80px", height: "80px",}}/>*/}
            {/*  소메뉴1*/}
            {/*</label>*/}
            {/*<label className="menu-group-label">*/}
            {/*  <TiFolderOpen style={{width: "80px", height: "80px",}}/>*/}
            {/*  움짤*/}
            {/*</label>*/}
            {/*<label className="menu-group-label">*/}
            {/*  <TiFolderOpen style={{width: "80px", height: "80px",}}/>*/}
            {/*  배경화면*/}
            {/*</label>*/}
            {/*<label className="menu-group-label">*/}
            {/*  <TiFolderOpen style={{width: "80px", height: "80px",}}/>*/}
            {/*  강아지만*/}
            {/*</label>*/}
              {menus && menus.map((menuItem, index) => (
                <label className="menu-group-label" key={index}>
                  <TiFolderOpen style={{ width: "80px", height: "80px" }} />
                  {menuItem.storageName}
                </label>
              ))}
          </div>) : (
            <div className="storage-menu-list">
              {/*<button className="menu-btn">소메뉴1</button>*/}
              {/*<button className="menu-btn">움짤</button>*/}
              {/*<button className="menu-btn">배경화면</button>*/}
              {/*<button className="menu-btn">강아지만</button>*/}
              {menus && menus.map((menuItem, index) => (
                <button className="menu-btn" key={index}>
                  {menuItem.storageName}
                </button>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default StorageList;
