import React, { useState } from 'react';
import Select from 'react-select';

const App = () => {
  const [selectedItem, setSelectedItem] = useState(null);
  const [selectedQuantity, setSelectedQuantity] = useState(null);
  const [quantityOptions, setQuantityOptions] = useState([]);
  const [isQuantityEnabled, setIsQuantityEnabled] = useState(false); // 갯수 옵션 활성화 여부

  // 품목 옵션
  const itemOptions = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' },
    { value: 'caramel', label: 'Caramel' }, // 추가된 옵션
  ];

  // 갯수 옵션 업데이트 함수
  const updateQuantityOptions = (selectedItem) => {
    setSelectedItem(selectedItem);
    switch (selectedItem.value) {
      case 'chocolate':
        setQuantityOptions([
          { value: '1', label: '1 개 - 재고 있음', available: true, defaultValue: false },
          { value: '2', label: '2 개 - 재고 있음', available: true, defaultValue: false },
          { value: '3', label: '3 개 - 재고 있음', available: true, defaultValue: false },
          { value: '0', label: '품절', available: false, defaultValue: true },
        ]);
        setIsQuantityEnabled(true); // 갯수 옵션 활성화
        break;
      case 'strawberry':
        setQuantityOptions([
          { value: '10', label: '10 개 - 재고 있음', available: true, defaultValue: false },
          { value: '20', label: '20 개 - 재고 있음', available: true, defaultValue: false },
          { value: '30', label: '30 개 - 재고 있음', available: true, defaultValue: false },
          { value: '0', label: '품절', available: false, defaultValue: true },
        ]);
        setIsQuantityEnabled(true); // 갯수 옵션 활성화
        break;
      case 'vanilla':
        setQuantityOptions([
          { value: '5', label: '5 개 - 재고 있음', available: true, defaultValue: false },
          { value: '10', label: '10 개 - 재고 있음', available: true, defaultValue: false },
          { value: '15', label: '15 개 - 재고 있음', available: true, defaultValue: false },
          { value: '0', label: '품절', available: false, defaultValue: true },
        ]);
        setIsQuantityEnabled(true); // 갯수 옵션 활성화
        break;
      case 'caramel':
        setQuantityOptions([
          { value: '4', label: '4 개 - 재고 있음', available: true, defaultValue: false },
          { value: '8', label: '8 개 - 재고 있음', available: true, defaultValue: false },
          { value: '12', label: '12 개 - 재고 있음', available: true, defaultValue: false },
          { value: '0', label: '품절', available: false, defaultValue: true },
        ]);
        setIsQuantityEnabled(true); // 갯수 옵션 활성화
        break;
      default:
        setQuantityOptions([]);
        setIsQuantityEnabled(false); // 갯수 옵션 비활성화
        break;
    }
  };

  // 갯수 옵션 선택 시 처리 함수
  const handleQuantityChange = (selectedOption) => {
    setSelectedQuantity(selectedOption);
  };

  return (
    <div>
      <h2>품목 옵션 선택:</h2>
      <Select
        options={itemOptions}
        onChange={(selectedOption) => {
          setSelectedQuantity(null); // 품목이 변경되면 갯수 선택 초기화
          updateQuantityOptions(selectedOption);
        }}
        value={selectedItem}
      />

      <h2>갯수 옵션 선택:</h2>
      <Select
        options={quantityOptions.map(option => ({
          ...option,
          isDisabled: option.value === '0' || option.defaultValue // value가 0이거나 기본으로 설정된 옵션인 경우 disable
        }))}
        onChange={handleQuantityChange}
        value={selectedQuantity}
        isDisabled={!isQuantityEnabled}
      />
    </div>
  );
};

export default App;
