import React, { useState } from 'react';
import Select from 'react-select';
import groupListData from './groupListData'; // 정적 데이터 파일 import

const App = () => {
  const [selectedOptions, setSelectedOptions] = useState(Array(groupListData.length).fill(null));
  const [isGroupEnabled, setIsGroupEnabled] = useState(Array(groupListData.length).fill(false));

  // 정적 데이터로서의 품목 그룹 리스트
  const groupList = groupListData;

  // 각 그룹의 옵션을 선택할 때 처리 함수
  const handleGroupChange = (selectedOption, groupIndex) => {
    const updatedOptions = [...selectedOptions];
    updatedOptions[groupIndex] = selectedOption;
    setSelectedOptions(updatedOptions);

    // 다음 그룹을 enable
    if (groupIndex < groupList.length - 1) {
      const updatedIsGroupEnabled = [...isGroupEnabled];
      updatedIsGroupEnabled[groupIndex + 1] = true;
      setIsGroupEnabled(updatedIsGroupEnabled);
    }
  };

  return (
    <div>
      {groupList.map((group, index) => (
        <div key={index}>
          <h2>{group.title}</h2>
          <Select
            options={group.options}
            onChange={(selectedOption) => handleGroupChange(selectedOption, index)}
            value={selectedOptions[index]}
            placeholder={`Select ${group.title}`}
            isDisabled={index === 0 ? false : !isGroupEnabled[index]}
          />
        </div>
      ))}
      <h2>선택한 옵션:</h2>
      {selectedOptions.map((selectedOption, index) => (
        <p key={index}>
          {groupList[index].title}: {selectedOption ? selectedOption.label : '-'}
        </p>
      ))}
    </div>
  );
};

export default App;
