import '../styles/storagelistcard.css';
import {useEffect, useState} from "react";
import React from "react";
import {dbService} from "../firebase";

const StorageListCard = ({ onNext }) => {
  const [clicked, setClicked] = useState(-1);
  const [list, setList] = useState("");
  const [stateIndex, setStateIndex] = useState(0)

  const handleClick = (index) => {
    setClicked(index);
    setStateIndex(index);
    console.log("clicked : {} ", clicked);
    console.log("stateIndex: {}",stateIndex);
  };

  const handleNext = () => {
    console.log("next: {}",stateIndex);
    onNext(stateIndex);

  }

  const getList = async () => {
    const dbLists = await dbService.collection("jjalbang-storageName").get();
    //  dbMenus.forEach((document) => {
    //   setMenus((prev) => [document.data(), ...prev]);
    // });
    const fetchedLists = dbLists.docs.map(document => document.data());
    setList(fetchedLists);
    console.log("list : {}",list);
  };

  useEffect(() => {
    getList();
  }, []);


  return (
    <div className="storage-list-container">
      <div className="storage-list">
        <div className="list-btns">
          <button className={`storage-list-btn ${clicked === 0 ? 'clicked' : ''}`}
                  key={0}
                  onClick={() => handleClick(0)}>새 저장소 추기</button>
        </div>
        {list && list.map((listItem, index) => (
          <button className={`storage-list-btn ${clicked === index+1 ? 'clicked' : ''}`}
                  key={index+1}
                  onClick={() => handleClick(index+1)}>
            {listItem.storageName}
          </button>
        ))}
        <div className="next-btns">
          <button className="pre-btn">이전</button>
          <button className="next-btn" onClick={handleNext}>다음</button>
        </div>
      </div>
    </div>
  );
}

export default StorageListCard;
