import Header from "../../components/Header";
import '../../styles/login.css';
import { authService } from "../../firebase";
import { GoogleAuthProvider, signInWithPopup } from "firebase/auth";
import { useState } from "react";

const Login = ({ setIsLoggedIn }) => {
  const [userData, setUserData] = useState(null);

  function handleGoogleLogin() {
    const provider = new GoogleAuthProvider();
    signInWithPopup(authService, provider)
      .then((data) => {
        setUserData(data.user);
        console.log(data);
        setIsLoggedIn(true); // 로그인 상태 업데이트
      })
      .catch((err) => {
        console.log(err);
      });
  }

  return (
    <div className="login-container">
      <Header text={"짤방저장소"}/>
      <div className="login">
        <img className="login-background-img" src="imgs/cute-doodle-flower-pattern.png" alt="배경화면" />
        <div className="login-components">
          <p className="login-title">로그인</p>
          <button className="login-btn" onClick={handleGoogleLogin}>
            <img className="login-logo" src="imgs/google-logo.png" alt="google-logo"/>
            <div className="login-text">구글로 로그인 하기</div>
          </button>
          {userData ? userData.displayName : null}
        </div>
      </div>
    </div>
  );
}

export default Login;
