import React, { useState } from 'react';
import './styles.css'; // styles.css ÌååÏùºÏùÑ import

const SelectBoxList = () => {
  const optionsData = [
    { label: 'fruits üçä', options: ['apple', 'orange', 'grape', 'melon'] },
    { label: 'Colors üéà', options: ['Red', 'orange', 'purple', 'green'] },
    { label: 'Foods üçü', options: ['pizza', 'ramen', 'kimchi', 'somori gukbob'] },
  ];

  const [activeIndex, setActiveIndex] = useState(null); // ÏÑ†ÌÉùÎêú Î∞ïÏä§ Ïù∏Îç±Ïä§ ÏÉÅÌÉú

  const handleBoxClick = (index) => {
    setActiveIndex(activeIndex === index ? null : index); // Î∞ïÏä§Î•º ÌÅ¥Î¶≠ÌïòÏó¨ ÌôúÏÑ±Ìôî/ÎπÑÌôúÏÑ±Ìôî
  };

  const handleOptionClick = (option) => {
    console.log('Selected option:', option);
    setActiveIndex(null); // ÏòµÏÖò ÏÑ†ÌÉù ÌõÑ Î∞ïÏä§ ÎπÑÌôúÏÑ±Ìôî
  };

  return (
    <div className="selectBoxList">
      {optionsData.map((data, index) => (
        <div key={index} className={`box ${activeIndex === index ? 'active' : ''}`}>
          <div className="selectBox2">
            <button className="label" onClick={() => handleBoxClick(index)}>
              {data.label}
            </button>
            <ul className="optionList">
              {data.options.map((option, idx) => (
                <li key={idx} className="optionItem" onClick={() => handleOptionClick(option)}>
                  {option}
                </li>
              ))}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
};

export default SelectBoxList;
