import Header from "../../components/Header";
import Message from "../../components/Message";
import StorageListCard from "../../components/StorageListCard";
import CreateStorage from "../../components/CreateStorage";
import '../../styles/main.css';
import {useState} from "react";
import {dbService} from "../../firebase";
const Main = () => {
  const [urlInput, setUrlInput] = useState("");
  const [showStorageList, setShowStorageList] = useState(false);
  const [showCreateStorage, setShowCreateStorage] = useState(false);
  const [storageName, setStorageName] = useState("");
  const [lastClickedButton, setLastClickedButton] = useState(null);

  const onChange = (e) => {
    e.preventDefault();
    setUrlInput(e.target.value);
  }

  const onSubmit = async (e) => {
    e.preventDefault();
    await dbService.collection("jjalbang").add({
      text: urlInput,
      crateAt: Date.now(),
    });
    setUrlInput("");
    setShowStorageList(true);
  }

  const handleBackToList = () => {
    setShowStorageList(false); // 이전 버튼 클릭 시 StorageListCard 숨기기
  }

  const handleCreateStorage = () => {
    setShowCreateStorage(true);
  }

  const handleStorageCreated = (name) => {
    // 저장소가 생성될 때 호출되는 콜백 함수
    setShowCreateStorage(false); // CreateStorage 숨기기
    setStorageName(name); // 새 저장소 이름 설정
  }

  const handleNext = () => {
    if (lastClickedButton === 3) {
      setShowCreateStorage(true);
    } else {
      setShowStorageList(false); // StorageListCard 숨기기
      setTimeout(() => {
        // 2초 후 메시지 표시
        setShowStorageList(false); // 다음에는 StorageListCard 다시 표시
      }, 2000);
    }
  }

  const handleButtonClick = (index) => {
    setLastClickedButton(index);
  };


  return (
    <div className="main-container">
      <Header text={"짤방저장소"}/>
      <div className="main">
        <img className="main-background-img" src="imgs/cute-doodle-flower-pattern.png" alt="배경화면" />
        <div className="main-create">
          {!showStorageList && (
            <>
          <p className="main-text">🔫 귀여운거 다 내꺼야 🔫</p>
          <input className="main-url-input"
                 type="text"
                 placeholder="URL을 입력하세요."
                 value={urlInput}
                 onChange={onChange}/>
          <button className="main-save-button" onClick={onSubmit}>저장하기</button>
            </>
          )}
          {showStorageList && <StorageListCard onBack={handleBackToList}
                                               onNext={handleNext}
                                               onButtonClick={handleButtonClick}/>}
          {showCreateStorage ? (
            <CreateStorage onCreate={handleStorageCreated} />
          ) : (
            // <Message text="저장되었습니다." />
            {}
          )}
        </div>
      </div>
    </div>
  );
};

export default Main;
