import React, { useState } from 'react';
import '../styles/Search.css';
import { useNavigate } from "react-router-dom";

const Search = () => {
  const [message, setMessage] = useState('');
  const navigate = useNavigate();
  const handleInputChange = (e) => {
    setMessage(e.target.value); // 입력된 텍스트를 상태로 업데이트
  }
  const handleChangePage = () => {
    console.log("페이지를 이동합니다.");
    navigate(`/chat/${message}`);
  }
  const handleSubmit = (e) => {
    e.preventDefault(); // 기본 제출 동작 방지
    handleChangePage(); // 페이지 이동 함수 호출
  }

  return (
    <div className='search-container'>
      <div className='contents'>
        <img className='content-logo' src='/imgs/icons-mouse.png' alt='logo'/>
        <p className='content-text'>
          무엇을 도와드릴까요?
        </p>
      </div>

      <div className='search'>
        <input className='search-input'
               type='text'
               value={message}
               placeholder='메세지를 입력하세요.'
               onChange={handleInputChange}
               onSubmit={handleChangePage}/>
        <button className='search-button'
                onClick={handleChangePage}>Enter</button>
      </div>
    </div>
  );
};

export default Search;
