import React, { useState } from 'react';

function App() {
// 예제용 아이템 데이터
  const items = [
    { id: 1, name: 'Item 1' },
    { id: 2, name: 'Item 2' },
    { id: 3, name: 'Item 3' },
    { id: 4, name: 'Item 4' },
    { id: 5, name: 'Item 5' },
  ];

  // 선택된 아이템을 관리할 상태
  const [selectedItems, setSelectedItems] = useState([]);

  // 아이템 클릭 핸들러
  const handleItemClick = (id) => {
    const index = selectedItems.indexOf(id);
    if (index === -1) {
      // 선택되지 않은 경우 선택 추가
      setSelectedItems([...selectedItems, id]);
    } else {
      // 선택된 경우 선택 제거
      const updatedSelection = selectedItems.filter((itemId) => itemId !== id);
      setSelectedItems(updatedSelection);
    }
  };

  return (
    <div>
      <h2>Group Selection Example</h2>
      <ul>
        {items.map((item) => (
          <li
            key={item.id}
            className={selectedItems.includes(item.id) ? 'isSelected' : ''}
            onClick={() => handleItemClick(item.id)}
          >
            {item.name}
          </li>
        ))}
      </ul>
      <p>Selected Items: {selectedItems.join(', ')}</p>
    </div>
  );
};

export default App;
