import React, { useEffect } from "react";

const Message = ({ text, onTimeout }) => {
  useEffect(() => {
    const timer = setTimeout(() => {
      onTimeout(); // 일정 시간이 지나면 onTimeout 함수를 호출하여 메시지를 사라지게 합니다.
    }, 500);

    return () => clearTimeout(timer); // 컴포넌트가 언마운트되기 전에 타이머를 클리어합니다.
  }, [onTimeout]);

  return (
    <div>
      <div>
        {text}
      </div>
    </div>
  );
}

export default Message;
