import "../styles/urlinput.css";
import {useState} from "react";
import {dbService} from "../firebase";

const UrlInput = ({ onNext}) => {
  const [urlInput, setUrlInput] = useState("");

  const onChange = (e) => {
    e.preventDefault();
    setUrlInput(e.target.value);
  }

  const onSubmit = async (e) => {
    e.preventDefault();
    // await dbService.collection("jjalbang-url").add({
    //   text: urlInput,
    //   crateAt: Date.now(),
    // });
    setUrlInput("");
    onNext(urlInput);
  }

  return (
    <div className="url-input-container">
      <div className="url-input">
          <p className="url-input-text">🔫 귀여운거 다 내꺼야 🔫</p>
          <input className="main-url-input"
                 type="text"
                 placeholder="URL을 입력하세요."
                 value={urlInput}
                 onChange={onChange}/>
          <button className="url-input-save-button" onClick={onSubmit}>저장하기</button>
      </div>
    </div>
  );
}

export default UrlInput;
