import Header from "../../components/Header";
import '../../styles/storagelist.css';
import { TiFolderOpen } from "react-icons/ti";
import {useState} from "react";
import Select from 'react-select';

const StorageList = () => {
  const shapeSelectList = [
    { value: "default", name: "형태"},
    { value: "group", name: "그룹형"},
    { value: "list", name: "목록형"},
  ];
  const [shapeSelected, setShapeSelected] = useState(shapeSelectList[0]);

  const handleShapeSelect = (selectedOption) => {
    setShapeSelected(e.target.value);
    console.log(shapeSelected);
  };

  return(
    <div className="storage-container">
      <Header text={"저장소"}/>
      <div className="storage">
        <img className="storage-background-img" src="imgs/cute-doodle-flower-pattern.png" alt="배경화면" />
        <div className="storage-components">
          <div className="storage-select-container">
            <div className="storage-selects">
              <Select className="shape-select"
                      onChange={handleShapeSelect}
                      value={shapeSelected}
                      options={shapeSelectList.map((item) => {
                    return <option value={item.value} key={item.value}>
                      {item.name}
                    </option>;
                  })}>
              </Select>
              <select>
                <option selected>나열순서</option>
                <option>최신순</option>
                <option>생성순</option>
              </select>
            </div>
          </div>

          {shapeSelected === "group" ? (<div className="storage-menu-group">
            <label className="menu-group-label">
              <TiFolderOpen style={{width: "80px", height: "80px",}}/>
              소메뉴1
            </label>
            <label className="menu-group-label">
              <TiFolderOpen style={{width: "80px", height: "80px",}}/>
              움짤
            </label>
            <label className="menu-group-label">
              <TiFolderOpen style={{width: "80px", height: "80px",}}/>
              배경화면
            </label>
            <label className="menu-group-label">
              <TiFolderOpen style={{width: "80px", height: "80px",}}/>
              강아지만
            </label>
          </div>) : (
            <div className="storage-menu-list">
              <button className="menu-btn">소메뉴1</button>
              <button className="menu-btn">움짤</button>
              <button className="menu-btn">배경화면</button>
              <button className="menu-btn">강아지만</button>
            </div>
          )}

        </div>
      </div>
    </div>
  );
}

export default StorageList;
