import React, { useState } from 'react';
import './styles.css'; // 필요한 경우 스타일 파일을 불러옵니다.

const SelectBox = () => {
  const [isActive, setIsActive] = useState(false); // 옵션 목록의 활성화 상태를 관리하는 상태 변수
  const [selectedOption, setSelectedOption] = useState('fruits'); // 선택된 옵션을 관리하는 상태 변수

  // 옵션 클릭 핸들러
  const handleOptionClick = (option) => {
    setSelectedOption(option.textContent); // 선택된 옵션 업데이트
    setIsActive(false); // 옵션 목록 닫기
  };

  // 라벨 클릭 핸들러
  const handleLabelClick = () => {
    setIsActive(!isActive); // 옵션 목록 열고 닫기
  };

  return (
    <div className={`selectBox ${isActive ? 'active' : ''}`}>
      <button className="label" onClick={handleLabelClick}>
        {selectedOption}
      </button>
      <ul className="optionList">
        <li className="optionItem" onClick={() => handleOptionClick(document.querySelector('.optionItem:nth-child(1)'))}>apple</li>
        <li className="optionItem" onClick={() => handleOptionClick(document.querySelector('.optionItem:nth-child(2)'))}>orange</li>
        <li className="optionItem" onClick={() => handleOptionClick(document.querySelector('.optionItem:nth-child(3)'))}>grape</li>
        <li className="optionItem" onClick={() => handleOptionClick(document.querySelector('.optionItem:nth-child(4)'))}>melon</li>
      </ul>
    </div>
  );
};

export default SelectBox;
