import React, { useState } from 'react';
import Select from 'react-select';
import groupListData from './optionData'; // 정적 데이터 파일 import

const App = () => {
  const [selectedOptions, setSelectedOptions] = useState({});

  // 정적 데이터로서의 품목 그룹 리스트
  const groupList = groupListData;

  // 각 그룹의 옵션을 선택할 때 처리 함수
  const handleGroupChange = (selectedOption, groupIndex) => {
    const updatedOptions = { ...selectedOptions, [groupIndex]: selectedOption };
    setSelectedOptions(updatedOptions);
  };

  // 갯수 선택 그룹에서 옵션의 label을 처리하는 함수
  const getQuantityOptionLabel = (option) => {
    if (option.label === '0') {
      return '0 (품절)';
    }
    return option.label;
  };

  // 갯수 선택 그룹에서 옵션의 disable 여부를 처리하는 함수
  const isQuantityOptionDisabled = (option) => {
    return option.label === '0';
  };

  return (
    <div>
      {groupList.map((group, index) => (
        <div key={index}>
          <h2>{group.title}</h2>
          <Select
            options={group.options}
            onChange={(selectedOption) => handleGroupChange(selectedOption, index)}
            value={selectedOptions[index]}
            placeholder={group.placeholder}
            getOptionLabel={group.title === '갯수 선택' ? getQuantityOptionLabel : undefined} // 갯수 선택 그룹의 label 표시 처리
            isDisabled={group.title === '갯수 선택' ? isQuantityOptionDisabled : false} // 갯수 선택 그룹의 disable 처리
          />
        </div>
      ))}
      <h2>선택한 옵션:</h2>
      {Object.keys(selectedOptions).map((key, index) => (
        <p key={index}>
          {groupList[key].title}: {selectedOptions[key].label}
        </p>
      ))}
    </div>
  );
};

export default App;
