            {/*      <p>저장 날짜: 2022-02-22</p>*/}
            {/*    </div>*/}
            {/*  )}*/}
            {/*</label>*/}

            {file.map((src, index) => {
              return (
                <label className={`obj ${clicked === index ? 'clicked' : ''}`} onClick={() => handleClick(index)}>
                <img className={`${clicked === index ? 'clicked-img' : ''}`} src={src}/>
