import Header from "../../components/Header";
import MenuBar from "../../components/MenuBar";
import '../../styles/login.css';
import { authService } from "../../firebase";
import { GoogleAuthProvider, signInWithPopup } from "firebase/auth";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [userData, setUserData] = useState(null);

  function handleGoogleLogin() {
    const provider = new GoogleAuthProvider(); // provider 구글 설정
    signInWithPopup(authService, provider) // 팝업창 띄워서 로그인
      .then((data) => {
        setUserData(data.user); // user data 설정
        console.log(data); // console에 UserCredentialImpl 출력
      })
      .catch((err) => {
        console.log(err);
      });
  }

  const navigate = useNavigate();

  return (
    <div className="login-container">
      <Header text={"짤방저장소"}/>
      <div className="login">
        <img className="login-background-img" src="imgs/cute-doodle-flower-pattern.png" alt="배경화면" />
        <div className="login-components">
          <p className="login-title">로그인</p>
          <button className="login-btn" >
            <img className="login-logo" src="imgs/google-logo.png" alt="google-logo"/>
            <div className="login-text" onClick={handleGoogleLogin}>구글로 로그인 하기</div>
          </button>
          {userData ? navigate('/') : null}
        </div>
      </div>
      <MenuBar/>
    </div>
  );
}

export default Login;
