import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import axios from 'axios';

const PrevImage = ({ imageUrl, index }) => {
  const [ogImageUrl, setOgImageUrl] = useState('');

  useEffect(() => {
    // URL에서 HTML 가져오기
    fetch(imageUrl)
      .then((res) => res.json())
      .then((res) => {
        console.log(res.data);

        const parser = new DOMParser();
        const htmlDoc = parser.parseFromString(response.data, 'text/html');
        const ogImageMetaTag = htmlDoc.querySelector('meta[property="og:image"]');
        if (ogImageMetaTag) {
          const imageUrl = ogImageMetaTag.getAttribute('content');
          setOgImageUrl(imageUrl);
        }
      })
      .catch(error => {
        console.error('Error fetching HTML:', error);
      });

    // axios.get(imageUrl)
    //   .then(response => {
    //
    //     const ogImageMetaTag = response.data.match(/<meta property="og:image" content="([^"]+)"/);
    //     if (ogImageMetaTag) {
    //       const Url = ogImageMetaTag[1];
    //       setOgImageUrl(Url);
    //     }
    //   })
    //   .catch(error => {
    //     console.error('Error fetching HTML:', error);
    //   });
  }, [imageUrl]);

  return (
    <>
      {/*<Helmet>*/}
      {/*  <meta property="og:url" content={Url}/>*/}
      {/*  <meta property="og:image" content=""/>*/}
      {/*</Helmet>*/}
      {/*<label>*/}
      {/*  <img src={imageUrl} alt={`img-${index}`} />*/}
      {/*</label>*/}

      {ogImageUrl && (
        <Helmet>
          <meta property="og:image" content={ogImageUrl} />
        </Helmet>
      )}

      {ogImageUrl && <img src={ogImageUrl} alt="OG Image" />}
    </>
  );
};

export default PrevImage;
