import React, { useState } from 'react';

const SelectBoxList = () => {
  const optionsData = [
    { label: 'fruits üçä', options: ['apple', 'orange', 'grape', 'melon'] },
    { label: 'Colors üéà', options: ['Red', 'orange', 'purple', 'green'] },
    { label: 'Foods üçü', options: ['pizza', 'ramen', 'kimchi', 'somori gukbob'] },
  ];

  const [isOpen, setIsOpen] = useState(false); // Î™©Î°ù Ïó¥Í≥† Îã´Ìûò ÏÉÅÌÉúÎ•º Í¥ÄÎ¶¨ÌïòÎäî ÏÉÅÌÉú Î≥ÄÏàò

  // Î≤ÑÌäº ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨
  const handleButtonClick = () => {
    setIsOpen(!isOpen); // isOpen ÏÉÅÌÉúÎ•º Î∞òÏ†ÑÏãúÏºú Î™©Î°ù Ïó¥Í≥† Îã´Í∏∞
  };

  return (
    <div className="selectBox">
      <button className="label" onClick={handleButtonClick}>fruits</button>
      <ul className={`optionList ${isOpen ? 'open' : ''}`}>
        <li className="optionItem">apple</li>
        <li className="optionItem">orange</li>
        <li className="optionItem">grape</li>
        <li className="optionItem">melon</li>
      </ul>
    </div>
  );
};

export default SelectBoxList;
