import React, { useState, useEffect } from 'react';
import groupListData from './groupListData'; // 정적 데이터 파일 import

const App = () => {
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [isGroupEnabled, setIsGroupEnabled] = useState(Array(groupListData.groupList.length).fill(false));
  const [optionList, setOptionList] = useState([]);

  // 정적 데이터로서의 품목 그룹 리스트
  const groupList = groupListData.groupList;
  const countList = groupListData.countList;

  // 각 그룹의 옵션을 선택할 때 처리 함수
  const handleGroupChange = (event, groupIndex) => {
    const selectedOption = optionList[groupIndex].find(option => option.value === event.target.value);
    const updatedOptions = [...selectedOptions];
    updatedOptions[groupIndex] = selectedOption;
    setSelectedOptions(updatedOptions);

    // 현재 그룹 다음의 그룹을 enable
    if (groupIndex < groupList.length - 1) {
      const updatedIsGroupEnabled = [...isGroupEnabled];
      updatedIsGroupEnabled[groupIndex + 1] = true;
      setIsGroupEnabled(updatedIsGroupEnabled);
    }
  };

  // 초기화 시 첫 번째 그룹 enable
  useEffect(() => {
    setIsGroupEnabled(updatedIsGroupEnabled => {
      const updated = [...updatedIsGroupEnabled];
      updated[0] = true;
      return updated;
    });
  }, []);

  // 각 셀렉터의 옵션 설정
  useEffect(() => {
    const updatedOptionList = groupList.map((group, index) => {
      if (index === 0) {
        // 첫 번째 셀렉터의 옵션 설정
        return group.options.map(option => {
          // 해당 동물과 관련된 모든 과일의 조합을 반복하여 remainCount를 확인
          const hasAvailableOption = countList.some(item => item.combination[0] === option && item.remainCount !== 0);
          const label = hasAvailableOption ? option : `${option} (품절)`;
          return {
            value: option,
            label: label,
            isDisabled: !hasAvailableOption
          };
        });
      } else if (index > 0 && selectedOptions[index - 1]) {
        // 이전 셀렉터의 선택 옵션과 현재 그룹의 옵션 조합에 따라 remainCount를 확인하여 품절 여부를 설정
        const previousSelectedOption = selectedOptions[index - 1];
        return group.options.map(option => {
          const hasAvailableOption = countList.some(item =>
            item.combination[index - 1] === previousSelectedOption.value &&
            item.combination[index] === option &&
            item.remainCount !== 0
          );
          const label = hasAvailableOption ? `${option} (${countList.find(item =>
            item.combination[index - 1] === previousSelectedOption.value &&
            item.combination[index] === option).remainCount} 개 남음)` : `${option} (품절)`;
          return {
            value: option,
            label: label,
            isDisabled: !hasAvailableOption
          };
        });
      } else {
        return [];
      }
    });

    // 마지막 셀렉터의 모든 옵션을 선택 가능하도록 설정
    // updatedOptionList[groupList.length - 1] = groupList[groupList.length - 1].options.map(option => ({
    //   value: option,
    //   label: option,
    //   isDisabled: false
    // }));

    setOptionList(updatedOptionList);
  }, [countList, groupList, selectedOptions]);

  return (
    <div>
      {groupList.map((group, index) => (
        <div key={index}>
          <h2>{group.title}</h2>
          <select
            value={selectedOptions[index] ? selectedOptions[index].value : ''}
            onChange={(event) => handleGroupChange(event, index)}
            disabled={index === 0 ? false : !isGroupEnabled[index]}
          >
            <option value="" disabled={true}>{group.title}</option>
            {optionList[index]?.map((option, idx) => (
              <option key={idx} value={option.value} disabled={option.isDisabled}>
                {option.label}
              </option>
            ))}
          </select>
        </div>
      ))}
      <h2>선택한 옵션:</h2>
      {selectedOptions.map((selectedOption, index) => (
        <p key={index}>
          {groupList[index]?.title}: {selectedOption ? selectedOption.label : '-'}
        </p>
      ))}
    </div>
  );
};

export default App;
