import '../styles/storagelistcard.css';
import { useState } from "react";
import React from "react";
const StorageListCard = ({ onNext }) => {
  const [clicked, setClicked] = useState(-1);
  const [clickedIndex, setClickedIndex] = useState(0);

  const handleClick = (index) => {
    setClicked((prevIndex) => prevIndex !== index ? index : -1);
    setClickedIndex(index);
    onNext(clickedIndex);
  };

  // const handleNext = () => {
  //   onNext(clickedIndex);
  // }

  return (
    <div className="storage-list-container">
      <div className="storage-list">
        <div className="list-btns">
          <button className={`storage-list-btn ${clicked === 0 ? 'clicked' : ''}`}
                  onClick={() => handleClick(0)}>소메뉴1</button>
          <button className={`storage-list-btn ${clicked === 1 ? 'clicked' : ''}`}
                  onClick={() => handleClick(1)}>움짤</button>
          <button className={`storage-list-btn ${clicked === 2 ? 'clicked' : ''}`}
                  onClick={() => handleClick(2)}>배경화면</button>
          <button className={`storage-list-btn ${clicked === 3 ? 'clicked' : ''}`}
                  onClick={() => handleClick(3)}>새 저장소 추기</button>
        </div>
        <div className="next-btns">
          <button className="pre-btn">이전</button>
          <button className="next-btn">다음</button>
        </div>
      </div>
    </div>
  );
}

export default StorageListCard;
