import Header from "../../components/Header";
import MenuBar from "../../components/MenuBar";
import Message from "../../components/Message";
import StorageListCard from "../../components/StorageListCard";
import CreateStorage from "../../components/CreateStorage";
import '../../styles/main.css';
import {useState} from "react";
import {dbService} from "../../firebase";
const Main = () => {
  const [urlInput, setUrlInput] = useState("");
  const [showStorageList, setShowStorageList] = useState(false);
  const [showCreateStorage, setShowCreateStorage] = useState(false);
  const [storageName, setStorageName] = useState("");

  const onChange = (e) => {
    e.preventDefault();
    setUrlInput(e.target.value);
  }

  const onSubmit = async (e) => {
    e.preventDefault();
    await dbService.collection("jjalbang").add({
      text: urlInput,
      crateAt: Date.now(),
    });
    setUrlInput("");
    setShowStorageList(true);
  }

  const handleBackToList = () => {
    setShowStorageList(false); // Ïù¥Ï†Ñ Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú StorageListCard Ïà®Í∏∞Í∏∞
  }

  const handleCreateStorage = () => {
    setShowCreateStorage(true);
  }

  const handleStorageCreated = (name) => {
    // Ï†ÄÏû•ÏÜåÍ∞Ä ÏÉùÏÑ±Îê† Îïå Ìò∏Ï∂úÎêòÎäî ÏΩúÎ∞± Ìï®Ïàò
    setShowCreateStorage(false); // CreateStorage Ïà®Í∏∞Í∏∞
    setStorageName(name); // ÏÉà Ï†ÄÏû•ÏÜå Ïù¥Î¶Ñ ÏÑ§Ï†ï
  }

  return (
    <div className="main-container">
      <Header text={"Ïß§Î∞©Ï†ÄÏû•ÏÜå"}/>
      <div className="main">
        <img className="main-background-img" src="imgs/cute-doodle-flower-pattern.png" alt="Î∞∞Í≤ΩÌôîÎ©¥" />
        <div className="main-create">
          {!showStorageList && (
            <>
          <p className="main-text">üî´ Í∑ÄÏó¨Ïö¥Í±∞ Îã§ ÎÇ¥Í∫ºÏïº üî´</p>
          <input className="main-url-input"
                 type="text"
                 placeholder="URLÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî."
                 value={urlInput}
                 onChange={onChange}/>
          <button className="main-save-button" onClick={onSubmit}>Ï†ÄÏû•ÌïòÍ∏∞</button>
            </>
          )}
          {showStorageList && <StorageListCard onBack={handleBackToList}/>}
          {showCreateStorage ? (
            <CreateStorage onCreate={handleStorageCreated} />
          ) : (
            <StorageListCard onAddNew={handleCreateStorage} newStorageName={storageName} />
          )}
          {/*<Message text="Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§." />*/}
        </div>
      </div>
    </div>
  );
};

export default Main;
