import Header from "../../components/Header";
import Message from "../../components/Message";
import StorageListCard from "../../components/StorageListCard";
import CreateStorage from "../../components/CreateStorage";
import UrlInput from "../../components/UrlInput";
import '../../styles/main.css';
import {useState} from "react";
import {dbService} from "../../firebase";
const Main = () => {
  const [step, setStep] = useState("url입력");
  const [valueData, setValueData] = useState({
    url: "",
    selectedStorage: 0,
    isCreate: false,
    newStorageName: "",
    selectedStorageName: "",
  });

  // const [selectData, setSelectData] = useState({
  //
  // })

  const [showMessage, setShowMessage] = useState(false);

  const handleShowMessage = async () => {
    setShowMessage(true);
    setTimeout(() => {
      setShowMessage(false);
      setStep("url입력"); // 다시 초기 단계로 돌아갑니다.
    }, 500); // 0.5초 후에 메시지를 감춥니다.

    await dbService.collection("jjalbang-url").add({
      text: valueData.url,
      selectedStorage: valueData.selectedStorage,
      crateAt: Date.now(),
    });
    console.log("valueData-selectStorage", valueData.selectedStorageName);
  };

  return (
    <div className="main-container">
      <Header text={"짤방저장소"}/>
      <div className="main">
        <img className="main-background-img" src="imgs/cute-doodle-flower-pattern.png" alt="배경화면" />
        <div className="main-create">
          {/*<UrlInput/>*/}
          {/*<StorageListCard/>*/}
          {/*<CreateStorage/>*/}
          {/*<Message text="저장되었습니다." />*/}
          <>
            {step === "url입력" && <UrlInput onNext={(data) => {
              setValueData({ ...valueData, url: data });
              setStep("저장소선택");
            }}/>}
            {step == "저장소선택" && <StorageListCard onNext={(data) => {
              setValueData({ ...valueData, selectedStorage: data, selectedStorageName: data });
              if (data === 0) {
                setStep("저장소생성");
              } else {
                setStep("저장완료");
              }
            }}/> }
            {step == "저장소생성" && <CreateStorage onNext={(data) => {
              setValueData({ ...valueData, newStorageName: data });
              handleShowMessage();
              setStep("저장완료");
            }}/> }
            {step == "저장완료" && <Message text="저장되었습니다." onTimeout={handleShowMessage}/> }
          </>
        </div>
      </div>
    </div>
  );
};

export default Main;
