import '../styles/createstorage.css';
import { useState, useEffect } from "react";
import { authService,dbService} from "../firebase";

const CreateStorage = ({ onNext }) => {
  const [storageName, setStorageName] = useState("");
  const handleChange = (e) => {
    setStorageName(e.target.value);
  }

  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = authService.onAuthStateChanged((user) => {
      if (user) {
        setUser(user);
      } else {
        setUser(null);
      }
    });
    return () => unsubscribe();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const uid = user.uid;
    await dbService.collection("jjalbang-storageName").add({
      storageName: storageName,
      crateAt: Date.now(),
      owner: uid,
    });
    setStorageName("");

    onNext(storageName)
  }

  return(
    <div className="create-storage-container">
      <div className="create-storage">
        <p className="create-storage-text">저장소의 이름을 입력해 주세요.</p>
        <input className="create-storage-input"
               type="text"
               value={storageName}
               onChange={handleChange}/>
        <div className="next-btns">
          <button className="pre-btn" >이전</button>
          <button className="next-btn" onClick={handleSubmit}>다음</button>
        </div>
      </div>
    </div>
  );
}

export default CreateStorage;
