import React, { useState } from 'react';
import Select from 'react-select';
import groupListData from './optionData'; // 정적 데이터 파일 import

const App = () => {
  const [selectedAnimal, setSelectedAnimal] = useState(null);
  const [selectedFood, setSelectedFood] = useState(null);
  const [isFoodEnabled, setIsFoodEnabled] = useState(false); // 음식 옵션 활성화 여부
  const [selectedOptionDisplay, setSelectedOptionDisplay] = useState(''); // 선택한 옵션 표시

  // 정적 데이터로서의 품목 그룹 리스트
  const groupList = groupListData;

  // 동물 종류 옵션
  const animalOptions = groupList.find(group => group.title === '동물 종류 선택').options;

  // 음식 옵션 업데이트 함수
  const updateFoodOptions = (selectedAnimal) => {
    setSelectedAnimal(selectedAnimal);
    const foodOptions = groupList.find(group => group.title === '음식 선택').options;
    setIsFoodEnabled(true); // 음식 옵션 활성화
    setSelectedFood(null); // 음식 선택 초기화
    setSelectedOptionDisplay(''); // 선택한 옵션 표시 초기화
    return foodOptions.filter(option => option.value.startsWith(selectedAnimal.value));
  };

  // 음식 옵션 선택 시 처리 함수
  const handleFoodChange = (selectedOption) => {
    setSelectedFood(selectedOption);
    setSelectedOptionDisplay(`${selectedAnimal.label} / ${selectedOption.label}`);
  };

  return (
    <div>
      {groupList.map((group, index) => (
        <div key={index}>
          <h2>{group.title}</h2>
          <Select
            options={group.options}
            onChange={(selectedOption) => {
              if (group.title === '동물 종류 선택') {
                setSelectedFood(null); // 동물 종류가 변경되면 음식 선택 초기화
                setSelectedOptionDisplay('');
                const foodOptions = updateFoodOptions(selectedOption);
                // setFoodOptions(foodOptions); // 이 부분은 필요없는 부분이므로 주석 처리
              }
              if (group.title === '음식 선택') {
                handleFoodChange(selectedOption)
              }
            }}
            value={group.title === '동물 종류 선택' ? selectedAnimal : selectedFood}
            isDisabled={false} // 항상 활성화 상태로 변경
            placeholder={group.placeholder}
          />
        </div>
      ))}
      {selectedOptionDisplay && (
        <p>선택한 옵션: {selectedOptionDisplay}</p>
      )}
    </div>
  );
};

export default App;
