import '../../styles/detailestorage.css';
import Header from "../../components/Header";
import {useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { dbService } from "../../firebase";
import { Helmet } from 'react-helmet';
import PrevImage from "../../components/PrevImage";

const DetaileStorage = () => {
  const [clicked, setClicked] =  useState(false);
  const handleClick = (id) => {
    setClicked(clicked === id ? null : id);
  }

  const { storageName } = useParams();

  const [file, setFile] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // 저장소 이름을 사용하여 해당 저장소에 대한 정보를 가져옵니다.
        const storageSnapshot = await dbService
          .collection("jjalbang-storageName")
          .where("storageName", "==", storageName)
          .get();

        if (!storageSnapshot.empty) {
          // 저장소 정보가 존재하면 해당 저장소의 인덱스 값을 가져옵니다.
          const storageIndex = storageSnapshot.docs[0].data().index;

          // 해당 인덱스에 해당하는 URL 목록을 가져옵니다.
          const urlsSnapshot = await dbService
            .collection("jjalbang-url")
            .where("selectedStorage", "==", storageIndex)
            .get();

          const urlsData = urlsSnapshot.docs.map((doc) => {
            //doc.data().url;
            console.log("doc.data()" , doc.data());
          });
          setFile(urlsData);
        } else {
          console.log("Storage not found");
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [storageName]);

  const [urls, setUrls] = useState([]);

  const getUrl = async () => {
    const dbUrls = await dbService.collection("jjalbang-url").get();
    const fetchedUrls = dbUrls.docs.map(document => document.data().url);
    setUrls(fetchedUrls);
  };

  useEffect(() => {
    getUrl();
  }, []);

  const sliceData = urls.map(item => [item]);

  return (
    <div className="detaile-storage-container">
      {/*<Header text={storageName}/>*/}
      <Header text="저장소이름"/>
      <div className="detaile-storage">
        <img className="detaile-storage-background-img" src="imgs/cute-doodle-flower-pattern.png" alt="배경화면"/>
        <div className="detaile-storage-components">
          <div className="detaile-select-container">
            <div className="detaile-select">
              <select>
                <option selected>나열순서</option>
                <option>최신순</option>
                <option>생성순</option>
              </select>
            </div>
          </div>

          <div className="objects">
            {/*<label className={`obj ${clicked === 1 ? 'clicked' : ''}`} onClick={() => handleClick(1)}>*/}
            {/*  <img className={`${clicked === 1 ? 'clicked-img' : ''}`} src="imgs/pexels-steve-johnson-1269968.jpg"/>*/}
            {/*  {clicked === 1 && (*/}
            {/*    <div className="clicked-info">*/}
            {/*      <p>저장 날짜: 2022-02-22</p>*/}
            {/*    </div>*/}
            {/*  )}*/}
            {/*</label>*/}
            {/*<label className={`obj ${clicked === 2 ? 'clicked' : ''}`} onClick={() => handleClick(2)}>*/}
            {/*  <img className={`${clicked === 2 ? 'clicked-img' : ''}`} src="imgs/pexels-pok-rie-2049422.jpg"/>*/}
            {/*  {clicked === 2 && (*/}
            {/*    <div className="clicked-info">*/}
            {/*      <p>저장 날짜: 2022-02-22</p>*/}
            {/*    </div>*/}
            {/*  )}*/}
            {/*</label>*/}
            {/*<label className={`obj ${clicked === 3 ? 'clicked' : ''}`} onClick={() => handleClick(3)}>*/}
            {/*  <img className={`${clicked === 3 ? 'clicked-img' : ''}`} src="imgs/pexels-pixabay-326055.jpg"/>*/}
            {/*  {clicked === 3 && (*/}
            {/*    <div className="clicked-info">*/}
            {/*      <p>저장 날짜: 2022-02-22</p>*/}
            {/*    </div>*/}
            {/*  )}*/}
            {/*</label>*/}
            {/*<label className={`obj ${clicked === 4 ? 'clicked' : ''}`} onClick={() => handleClick(4)}>*/}
            {/*  <img className={`${clicked === 4 ? 'clicked-img' : ''}`} src="imgs/pexels-katja-592077.jpg"/>*/}
            {/*  {clicked === 4 && (*/}
            {/*    <div className="clicked-info">*/}
            {/*      <p>저장 날짜: 2022-02-22</p>*/}
            {/*    </div>*/}
            {/*  )}*/}
            {/*</label>*/}
            {/*<label className={`obj ${clicked === 5 ? 'clicked' : ''}`} onClick={() => handleClick(5)}>*/}
            {/*  <img className={`${clicked === 5 ? 'clicked-img' : ''}`} src="imgs/pexels-eberhard-grossgasteiger-844297.jpg"/>*/}
            {/*  {clicked === 5 && (*/}
            {/*    <div className="clicked-info">*/}
            {/*      <p>저장 날짜: 2022-02-22</p>*/}
            {/*    </div>*/}
            {/*  )}*/}
            {/*</label>*/}
            {/*<label className={`obj ${clicked === 6 ? 'clicked' : ''}`} onClick={() => handleClick(6)}>*/}
            {/*  <img className={`${clicked === 6 ? 'clicked-img' : ''}`} src="imgs/pexels-chevanon-photography-1108099.jpg"/>*/}
            {/*  {clicked === 6 && (*/}
            {/*    <div className="clicked-info">*/}
            {/*      <p>저장 날짜: 2022-02-22</p>*/}
            {/*    </div>*/}
            {/*  )}*/}
            {/*</label>*/}
            {/*<label className={`obj ${clicked === 7 ? 'clicked' : ''}`} onClick={() => handleClick(7)}>*/}
            {/*  <img className={`${clicked === 7 ? 'clicked-img' : ''}`} src="imgs/pexels-aleksandar-pasaric-1415734.jpg"/>*/}
            {/*  {clicked === 7 && (*/}
            {/*    <div className="clicked-info">*/}
            {/*      <p>저장 날짜: 2022-02-22</p>*/}
            {/*    </div>*/}
            {/*  )}*/}
            {/*</label>*/}

            {/*{sliceData.map((url, index) => (*/}
            {/*  <label key={index} className={`obj ${clicked === index ? 'clicked' : ''}`} onClick={() => handleClick(index)}>*/}
            {/*    <img className={`${clicked === index ? 'clicked-img' : ''}`} src={url[index]} alt={`img-${index}`} />*/}
            {/*    {clicked === index && (*/}
            {/*      <div className="clicked-info">*/}
            {/*        <p>저장 날짜: 2022-02-22</p>*/}
            {/*        <p>{url[index]}</p>*/}
            {/*      </div>*/}
            {/*    )}*/}
            {/*  </label>*/}
            {/*))}*/}

            {sliceData.map((urlArray, parentIndex) => (
              <>
              {urlArray.map((url, index) => (
                <label key={index} className={`obj ${clicked === parentIndex ? 'clicked' : ''}`} onClick={() => handleClick(parentIndex)}>
                  <PrevImage className={`${clicked === parentIndex ? 'clicked-img' : ''}`} imageUrl={url} index={parentIndex} />
                  {clicked === parentIndex && (
                    <div className="clicked-info">
                      <p>저장 날짜: 2022-02-22</p>
                      <p>{url}</p>
                    </div>
                  )}
                </label>
              ))}
              </>
            ))}
          </div>

        </div>
      </div>
    </div>
  );
}

export default DetaileStorage;
