import React, { useState } from 'react';
import Select from 'react-select';

const App = () => {
  const [selectedItem, setSelectedItem] = useState(null);
  const [quantityOptions, setQuantityOptions] = useState([]);

  // 품목 옵션
  const itemOptions = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' },
    { value: 'caramel', label: 'Caramel' }, // 추가된 옵션
  ];

  // 갯수 옵션 (품목에 따라 동적으로 변경)
  const updateQuantityOptions = (selectedItem) => {
    switch (selectedItem.value) {
      case 'chocolate':
        setQuantityOptions([
          { value: '1', label: '1' },
          { value: '2', label: '2' },
          { value: '3', label: '3' },
        ]);
        break;
      case 'strawberry':
        setQuantityOptions([
          { value: '10', label: '10' },
          { value: '20', label: '20' },
          { value: '30', label: '30' },
        ]);
        break;
      case 'vanilla':
        setQuantityOptions([
          { value: '5', label: '5' },
          { value: '10', label: '10' },
          { value: '15', label: '15' },
        ]);
        break;
      case 'caramel':
        setQuantityOptions([
          { value: '4', label: '4' },
          { value: '8', label: '8' },
          { value: '12', label: '12' },
        ]);
        break;
      default:
        setQuantityOptions([]);
        break;
    }
    setSelectedItem(selectedItem);
  };

  return (
    <div>
      <h2>품목 옵션 선택:</h2>
      <Select
        options={itemOptions}
        onChange={updateQuantityOptions}
        value={selectedItem}
      />

      <h2>갯수 옵션 선택:</h2>
      <Select options={quantityOptions} />
    </div>
  );
};

export default App;
